import React, { useEffect } from "react";
import "./screens.css";
import { useDarkMode } from "../context/DarkModeContext";

export const Home = () => {
    const { isDarkMode } = useDarkMode();

    const backgroundClass = isDarkMode ? 'dark-background' : 'background';

  // Use useEffect to update the body color when isDarkMode changes
  useEffect(() => {
    document.body.className = backgroundClass;
  }, [backgroundClass]);


    return (
      <div>
        <h1 className={isDarkMode ? 'dark-title' : 'title'}>
          Home
        </h1>
      </div>
    );
};
